import styles from './Header.module.css';
import logo from '../images/logo.png';
import {Link} from 'react-router-dom';
import search from '../images/search-normal.png';
import bag from '../images/bag.png';
import user from '../images/user.png';
import menu from '../images/menu.svg';
import close from '../images/close-circle.svg';

export function Header(){
    function handleClick(e){
        var card = document.querySelector('.menuCard');
        var nav = document.querySelector('.nav');
        var link = document.querySelectorAll('.link');
        if(card.classList.contains('active')){
            card.classList.remove('active');
            nav.classList.remove('active');
            for(var i = 0; i < link.length; i++) link[i].classList.remove('active');
        }
        else{
            card.classList.add('active');
            nav.classList.add('active');
            for(var i = 0; i < link.length; i++) link[i].classList.add('active');    
        }
    }

    return(
        <div className={styles.content}>
            <div className={styles.logo}>
                <img src={logo} alt="Logo" />
            </div>
            <img src={menu} alt="" className={styles.menu} onClick={handleClick}/>
            <div className={styles.nav}>
                <Link to="/" className={styles.link}>Home</Link>
                <Link to="/" className={styles.link}>Products</Link>
                <Link to="/" className={styles.link}>Blog</Link>
                <Link to="/" className={styles.link}>FAQ</Link>
                <Link to="/" className={styles.link}>Contact Us</Link>
            </div>
            <p className={styles.company}>Tech Heim</p>
            <div className={styles.buttons}>
                <Link to="/" className={styles.icon}><img src={search} alt="search" /></Link>
                <Link to="/" className={styles.icon}><img src={bag} alt="bag" /></Link>
                <Link to="/" className={styles.icon}><img src={user} alt="user" /></Link>
            </div>
            <div className={styles.menuCard}>
                <div className={styles.menuCardTop}>
                    <img src={logo} alt="" />
                    <img src={close} alt="" className={styles.closeBtn} onClick={handleClick}/>
                </div>
            </div>
        </div>
    );
}
