import styles from './CartPage.module.css';
import {Header} from './Header';
import {Footer} from './Footer';
import { ProductInCart } from './ProductInCart';
import { useState } from 'react';
import stepper from '../images/stepper.png';
import stepper1 from '../images/stepper1.png';
import stepper2 from '../images/stepper2.png';
import stepper3 from '../images/stepper3.png';
import stepper4 from '../images/stepper4.png';
import stepper5 from '../images/stepper5.png';
import stepper6 from '../images/stepper6.png';

export function CartPage(){
    const [cart, setCart] = useState(true);
    const [checkout, setCheckout] = useState(false);
    const [payment, setPayment] = useState(false);

    /*
        <img src={cart ? stepper : stepper3} alt="" className={cart ? `${styles.stepper} ${styles.active}` : styles.stepper}/>
        <img src={checkout ? stepper4 : (payment ? stepper6 : stepper1)} alt="" className={checkout ? `${styles.stepper} ${styles.active}` : styles.stepper}/>
        <img src={payment ? stepper5 : stepper2} alt="" className={payment ? `${styles.stepper} ${styles.active}` : styles.stepper}/>
     */

    return(
        <div className={styles.wrapper}>
            <Header/>
            <div className={styles.content}>
                <div className={styles.nav}>
                    <div className={cart ? `${styles.stepper} ${styles.active}` : styles.stepper}>Cart</div>
                    <div className={checkout ? `${styles.stepper} ${styles.active}` : styles.stepper}>Checkout</div>
                    <div className={payment ? `${styles.stepper} ${styles.active}` : styles.stepper}>Payment</div>
                </div>
                <div className={cart ? `${styles.cart} ${styles.active}` : styles.cart}>
                    <div className={styles.cardContainer}>
                        <ProductInCart/>
                        <ProductInCart/>
                        <ProductInCart/>
                        <ProductInCart/>
                        <ProductInCart/>
                        <ProductInCart/>
                    </div>
                    <div className={styles.details}>
                        <h1 className={styles.detailsTitle}>Payment details</h1>
                        <div className={styles.total}>
                            <p className={styles.totalText}>Grand Total</p>
                            <p className={styles.totalText}>$3000</p>
                        </div>
                        <div className={styles.detailsButton} onClick={e => {setCart(false); setCheckout(true);}}>Proceed to checkout</div>
                    </div>
                </div>
                <div className={checkout ? `${styles.checkout} ${styles.active}` : styles.checkout}>
                    <div className={styles.userInfo}>

                    </div>
                    <div className={styles.order}>

                    </div>
                </div>
                <div className={payment ? `${styles.payment} ${styles.active}` : styles.payment}>

                </div>
            </div>
            <Footer/>
        </div>
    );
}
